scan_table_check.script:
(function (engine) {

	Debug('Running check: CodeSanity - Application ATF test suite - on record '+x_snc_devtools.RenderRecordSummary(engine.current));

	if (x_snc_codesanity.IsCheckApplicableToRecord())
	{

		Debug('current sys_id: '+engine.current.sys_id);
		Debug(x_snc_devtools.GetAppName(engine.current.sys_id));

		var grSuite = new GlideRecord('sys_atf_test_suite');
		grSuite.addQuery('sys_scope', engine.current.sys_id);
		grSuite.addQuery('name', x_snc_devtools.GetAppName(engine.current.sys_id));
		grSuite.query();
		if (grSuite.getRowCount() == 0)
		{
			engine.finding.setCurrentSource(current);
			engine.finding.increment();
		}
	}

})(engine);
