sys_extension_point.example:
var CodeSanity = Class.create();
CodeSanity.prototype = {
	initialize: function()
	{	
	},

	GetExecutionRules: function(rules)
	{
		var IsValidFunction = x_snc_devtools.IsValidFunction;
		if (IsValidFunction(x_this_app_scope.CodeSanityGetExecutionRules))
		{
			return CodeSanityGetExecutionRules(rules);
		}
		return false;
	},	

	type: 'CodeSanity'
};
