sys_script_include.script:
function IsCheckApplicableToRecord(grRecord,grCheck)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetCallStack = x_snc_devtools.GetCallStack;
	var GetValue = x_snc_devtools.GetValue;
	var GetRecord = x_snc_devtools.GetRecord;

	if (IsValidRecord(grCheck) == false)
	{
		var aStack = GetCallStack();
		grCheck = GetRecord(GetValue(aStack,1,'sys_class_name'),GetValue(aStack,1,'sys_id'));
		if (IsValidRecord(grCheck) == false)
		{
			return false;
		}
	}
	//var RenderRecordSummary = x_snc_devtools.RenderRecordSummary;
	//Log('IsCheckApplicableToRecord: '+RenderRecordSummary(grCheck)+'\n'+RenderRecordSummary(grRecord));
	return true;
}
