function CodeSanityScanApp(strAppSysId)
{

	var dialogClass = GetGlideDialogClass();
	var dd = new dialogClass("simple_progress_viewer", true, "40em", "10em");


	dd.setTitle("CodeSanity");

    dd.setPreference("sysparm_ajax_processor_app_sys_id", strAppSysId);
    dd.setPreference("sysparm_ajax_processor", 'x_snc_codesanity.CodeSanityClientAPI');

    dd.setPreference("sysparm_renderer_progress_title", "Scan");

	dd.setPreference("sysparm_button_view_results", "View Results");
	dd.setPreference("sysparm_button_close", "Close");


    dd.on("executionComplete", function(tracker)
	{
		console.log('complete: '+RenderValue(tracker));

		var strResultSysId = GetStringValue(GetValue(tracker,'result','sys_id'));
		console.log(strResultSysId);

		var buttonViewResults = GetElementById('sysparm_button_view_results');

		if (buttonViewResults)
		{
			if (IsValidSysId(strResultSysId) == true)
			{
				buttonViewResults.on('click', function()
					{
						RedirectToRecord('scan_result',strResultSysId,'',true);
					});
			}
			else
			{
				buttonViewResults.style.display = "none";
			}
		}

		var buttonClose = GetElementById('sysparm_button_close');
		console.log('buttonClose: '+RenderValue(buttonClose));

        if (buttonClose)
		{
            buttonClose.on('click', function()
				{
					dd.destroy();
				});
        }

    });

	dd.on("beforeclose", function()
	{
		//refreshNav();
		reloadWindow(window); //reload current form after closing the progress viewer dialog
	});	

    dd.render();	

	//var map = new GwtMessage().getMessages(["Refresh Repository", "Close"]);
/*
	dd.setTitle(map["Refresh Repository"]);

	dd.setPreference('sysparm_ajax_processor', 'SourceControlAjax');
	dd.setPreference('sysparm_ajax_processor_type','refresh_repo');
	dd.setPreference('sysparm_ajax_processor_sys_repo_config_id', rowSysId);

	dd.setPreference('sysparm_renderer_progress_title', "Run Code Sanity check on application");
	dd.setPreference('sysparm_button_close', "Close");

	dd.on("executionComplete", function(trackerObj) {
		var closeBtn = $("sysparm_button_close");
		if (closeBtn) {
			closeBtn.onclick = function() {
				dd.destroy();
			};
		}
	});

	dd.render();	
	*/



}
